document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll(".shuffle-link").forEach((e=>{const t=e.textContent;e.innerHTML="";let n=!1;for(let n of t)if(" "===n)e.appendChild(document.createTextNode(" "));else{const t=document.createElement("span");t.textContent=n,e.appendChild(t)}e.addEventListener("mouseenter",(()=>{n||(e.querySelectorAll("span").forEach((e=>{const t=2*(Math.random()-.5),n=8*(Math.random()-.5),o=30*(Math.random()-.5);e.style.transform=`translate(${t}px, ${n}px) rotate(${o}deg)`})),n=!0)})),e.addEventListener("mouseleave",(()=>{e.querySelectorAll("span").forEach((e=>{e.style.transform="none"})),n=!1}))}))}));